#--Python Mini Project--#LIBRARY MANAGEMENT SYSTEMimport sysclass Library :    '''    This class is used to keep Records of Books Library    This class contains Basically Four Modules :    1. Display Books 2. Lend Book 3. Add Book 4.Return Book    '''    #Constructor or init Method    def __init__(self,bookList,library_name):        self.bookList=bookList.copy()        self.library_name=library_name        self.lend_books={}        self.remain_books=bookList.copy()    #Represent Information    def __repr__(self):        return f'''Welcome to {self.library_name} Library\nSelect options from the Listed Below :\n1. Display Books\n2. Lend Books\n3. Add any Book\n4. Return book'''    #Display Book instance method    def display(self):        print("Library contains Books : ")        for i in range(len(self.bookList)):            print(f'{i+1}. {self.bookList[i]}')        print()    #lend Books instance method    def lend(self):        book_name = input("Enter Book Name for Check :")        if book_name in self.bookList:            if book_name not in self.remain_books :                print(f"{self.lend_books[book_name]} is already Booked this Book")            else:                person_name=input("Enter your Name : ")                for book in self.lend_books:                    if self.lend_books[book]==person_name:                        print(f"Sorrey ,You Already take a Book {book}")                        break                else:                    print('Okay This Book is Booked for you , You can take The Book Now. \n')                    (self.lend_books[book_name])=person_name                    index=self.remain_books.index(book_name)                    del self.remain_books[index]        else:            print("You Entered Incorrect Book Name , Try Again")            self.lend()    #Adding Book instance Method    def add (self):        add_book_name=input("Enter Book Name for Adding : ")        self.boo